children.script:
var self = this;
var loading = V('loading');
loading.$.css({
    color: '#ff0000'
});
self.$.css('cursor', 'pointer');
self.on('click', function() {
    var used = !!self.data('used');
    var result = !!self.data('result');

    if (!used) {
        self.data('used', true);
        self.style({
            transform: { originX: 0.5, originY: 0.7 }
        });

        self.to({
            transform: { rotate: 2700 }
        }, 3000, 'easeInOut', function() {
            self.data('result', true);
        }).to(1000, function() {
            var children = self.parent.children;
            children[2].to('fadeOut');
            children[3].to('fadeOut');
            children[4].style('visible', true);
            children[4].play('all');
            children[4].to('fadeIn');
        })
    } else if (result) {
        alert('你已经抽过奖了')
    }
})

this.on('click', function() {
    var bbc = V('display_bbc');
    var x = bbc.x;
    bbc.to({ x: 500, transform: { rotate: 360 } }).to({ x: x, transform: { rotate: 0 } });
})

this.style({
    x: 0, y: -150
});
this.to({
    y: 100
}, 600, 'bounceOut');
alert(123);
